mapper.update:
UPDATE moamoamember SET
pw = #{pw}
WHERE id = #{id}

UPDATE moamoamember SET
nik = #{nik}
WHERE id = #{id}

mapper.select:
SELECT * FROM ( SELECT rownum rnum, a.* FROM (SELECT * FROM moamoa WHERE hotelname is not null
AND hotelname LIKE '%'|| #{keyword}||'%'
AND hoteladdr LIKE '%'|| #{keyword}||'%'
AND hotelname LIKE '%'||#{keyword}||'%' OR hoteladdr LIKE '%'||#{keyword}||'%'
AND title LIKE '%'|| #{keyword}||'%'
)a) WHERE rnum BETWEEN #{pageStart} AND #{perPageNum}

mapper.update:
UPDATE moaboard
	SET viewcnt = viewcnt +1
	WHERE bno = #{bno}

choose.when:
UPDATE moaboard SET 
title= #{title}, nik=#{nik},

	filename = #{filename},
	filesize = #{filesize},

content=#{content}
WHERE bno = #{bno}

choose.otherwise:
UPDATE moaboard SET 
title= #{title},nik=#{nik},

	filename = #{filename}, 
	filesize = #{filesize}

WHERE bno = #{bno}

mapper.select:
SELECT * FROM moareply
WHERE bno =#{bno}
ORDER BY rno ASC
